mapper.select:

SELECT	CODE_ID codeValue,
		CODE_NM codeName
FROM	TB_ACCOUNT
WHERE	BLD_NO = #{bldNo}
  AND   GROUP_CODE_ID = #{groupCodeId}
ORDER BY GROUP_CODE_ID, CODE_ID

mapper.insert:
INSERT INTO TB_SLIP(
	BLD_NO
	, SLIP_NO
	, SLIP_DETAIL_NO
	, DEAL_DATE
	, CNT_CLS
	, TAX_CLS
	, DEAL_TYPE
	, DEAL_CUST
	, DEAL_DTLS
	, ACNT_LCLAS
	, ACNT_MLSFC
	, ACNT_SCLAS
	, SUPLY_PRICE
	, SPLPC_AM
	, VAT
	, USE_YN
	, REGISTER
	, UP_DATETIME
) VALUES(
	#{bldNo}
	, CONCAT(#{dealDate}, (select if(count(slip_no)=0, '0001', lpad(max(right(slip_no, 4))+1, 4, '0'))
							from tb_slip t1
							where deal_date = #{dealDate}) )
	, (select if(count(slip_detail_no)=0, '1', max(slip_detail_no)+1)
		from tb_slip t2
		where deal_date = #{dealDate}
		  and slip_no = CONCAT(#{dealDate}, (select if(count(slip_no)=0, '0001', lpad(max(right(slip_no, 4))+1, 4, '0'))
										 	   from tb_slip t3
											  where deal_date = #{dealDate}) ) )
	, #{dealDate}
	, #{cntCls}
	, #{taxCls}
	, #{dealType}
	, #{dealCust}
	, #{dealDtls}
	, #{acntLclas}
	, #{acntMlsfc}
	, #{acntSclas}
	, #{suplyPrice}
	, #{splpcAm}
	, #{vat}
	, 'Y'
	, #{register}
	, now()
)
